import axios from 'axios'

export const fetchHouses = async () => {
  return await  axios(`/api/listing`).then(res => res.data)

  // /?limit=${limit}&offset=${offset}
  
}    

export const fetchHousesById = async (id) => {
  console.log(id)
  return await  axios.get(`/listing/house/${id}`
  
  ).then(res =>  res.data)


  
  
} 
