import React, {Component} from 'react'
import { connect } from 'react-redux'

import {searchHouse} from 'actions/index'

class Search extends Component {
    
    state = {
        value: ''
  }

  handleChange =(event)=> {
      
    this.setState({value: event.target.value});
    // console.log(event.target.value)
                    
  }

  handleSubmit =(event)=>{
    event.preventDefault();
    this.props.searchHouse(this.state.value);
  }

    render(){
        return (
            <div >
                <h3 className='lead'>Quick search</h3>
                <div className='input-group'>
                    <form onSubmit={this.handleSubmit}>
                        <input 
                        placeholder="Search product here..."
                        className='form-control'
                        type='text'
                        onChange={this.handleChange}
                        
                        >
                        </input>
                        
                    </form>
                    <span className='input-group-btn'>
                       
                        <button className="btn btn-primary" ><i className="fa fa-search" aria-hidden="true" onClick={this.handleSubmit}></i></button>
                    </span>
                </div>

            </div>
        )
    }
}

const mapDispatchToProps = {
    searchHouse
}
export default connect(null, mapDispatchToProps)(Search)