import * as R from 'ramda'

import {
  FETCH_HOUSES_SUCCESS,
  SEARCH_HOUSE
      } from 'actionTypes'

const initialState = {
  ids: [],
  search: ''
}


export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_HOUSES_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })

      case SEARCH_HOUSE:
        return R.merge(state, {
            search: payload
        })

    default:
      return state;
  }
}