import * as R from 'ramda'

import {
  FETCH_HOUSES_SUCCESS,
  FETCH_SEARCH_HOUSE_SUCCESS
} from 'actionTypes'

const initialState = {
  ids: [],
  search: {
    product: '',
    garage: '',
    type: '',
    increase: 'false'
   

  }
 
}


export default (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_HOUSES_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })

    case FETCH_SEARCH_HOUSE_SUCCESS:
      return R.merge(state, {
        search: payload
      })

    default:
      return state;
  }
}